function createCatFile(email, api_key) {
  return `cat >~/.netrc <<EOF
machine api.heroku.com
    login ${email}
    password ${api_key}
machine git.heroku.com
    login ${email}
    password ${api_key}
EOF`;
}

const api_key = core.getInput("heroku_api_key");
const email = core.getInput("heroku_email");
const app_name = core.getInput("heroku_app_name");
const promote_to_app = core.getInput("heroku_promote_to_app");

try {
  let script = `heroku pipelines:promote -a ${app_name}`;
  let statusValue = `Successfully promoted heroku app ${app_name}`;
  let promoteGroupMsg = `Promoting app ${app_name}`;
  script += promote_to_app ? ` --to ${promote_to_app}` : "";
  statusValue += promote_to_app ? ` to ${promote_to_app} app(-s)` : "";
  promoteGroupMsg += promote_to_app ? ` to ${promote_to_app} app(-s)` : "";
  core.startGroup("Heroku sign in");
  child_process.execSync(createCatFile(email, api_key));
  console.log("Created and wrote to ~./netrc");
  child_process.execSync("heroku login");
  console.log("Successfully logged into Heroku");
  core.endGroup();
  core.startGroup(promoteGroupMsg);
  child_process.execSync(script);
  core.setOutput("status", statusValue);
  core.endGroup();
} catch (err) {
  core.setFailed(err.toString());
}
